site.headScript:
var text = '{my}Bookmarks|dev';
var textSpans = [];
var i =0;
function createTextSpans() {
  
    var textSpan = document.createElement('span');
    textSpan.classList.add('text-span');
    textSpan.textContent = text[i];
    textSpans.push(textSpan);
    document.getElementById('animated-text').appendChild(textSpan);
        textSpans[i].style.opacity = 1;
        if(i>0 && i<3){
            textSpans[i].style.color = "red";
        }
    textSpans[i].style.transform = 'translateX(0)';
    if(i < text.length){ i++;
  setTimeout(createTextSpans, 100);}
}


window.addEventListener('load', function() {
    createTextSpans();
}
);

function filterList(tag) {
  var items = document.getElementsByClassName("item");
  for (var i = 0; i < items.length; i++) {
    if (tag == "all" || items[i].classList.contains(tag)) {
      items[i].style.display = "block";
    } else {
      items[i].style.display = "none";
    }
  }
}

var searchInput = document.getElementById("search");
searchInput.addEventListener("keyup", function (event) {
  var searchText = event.target.value.toLowerCase();
  var items = document.getElementsByClassName("item");
  for (var i = 0; i < items.length; i++) {
    var itemText = items[i].textContent.toLowerCase();
    if (itemText.indexOf(searchText) > -1) {
      items[i].style.display = "block";
    } else {
      items[i].style.display = "none";
    }
  }
});
